import "core-js/modules/es.iterator.map.js";
import { ref, reactive, computed, watch, nextTick, onMounted } from 'vue';
import { message } from 'ant-design-vue';
import dayjs from 'dayjs';
import { hardwareAssetApi } from '@/api/hardwareAsset';
import { supplierApi } from '@/api/supplier';
import { userApi } from '@/api/user';
// 移除了大量不必要的图标导入

// Props

export default {
  __name: 'SoftwareAssetForm',
  props: {
    visible: {
      type: Boolean,
      default: false
    },
    assetData: {
      type: Object,
      default: null
    },
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  emits: ['update:visible', 'success'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;

    // Emits
    const emit = __emit;

    // 响应式数据
    const formRef = ref();
    const submitLoading = ref(false);
    const userLoading = ref(false);
    const supplierLoading = ref(false);
    const supplierOptions = ref([]);
    const supplierContacts = ref([]);
    const specifications = ref([{
      key: '',
      value: ''
    }]);

    // 表单数据
    const formData = reactive({
      asset_tag: '',
      model: '',
      asset_owner: '',
      supplier: '',
      purchase_date: '',
      project_source: '',
      manufacturer: '',
      serial_number: '',
      room: '',
      cabinet: '',
      u_position: '',
      dimensions: '',
      warranty_type: '',
      warranty_date_range: null
    });

    // 选项数据
    const assetTagOptions = ref([{
      label: 'SRV-001',
      value: 'SRV-001'
    }, {
      label: 'SRV-002',
      value: 'SRV-002'
    }, {
      label: 'NET-001',
      value: 'NET-001'
    }, {
      label: 'NET-002',
      value: 'NET-002'
    }, {
      label: 'STG-001',
      value: 'STG-001'
    }, {
      label: 'STG-002',
      value: 'STG-002'
    }]);
    const manufacturerOptions = ref([{
      label: '华为',
      value: '华为'
    }, {
      label: '思科',
      value: '思科'
    }, {
      label: '戴尔',
      value: '戴尔'
    }, {
      label: '惠普',
      value: '惠普'
    }, {
      label: '联想',
      value: '联想'
    }, {
      label: '浪潮',
      value: '浪潮'
    }, {
      label: '新华三',
      value: '新华三'
    }, {
      label: '锐捷',
      value: '锐捷'
    }]);
    const dimensionOptions = ref([{
      label: '1U',
      value: '1U'
    }, {
      label: '2U',
      value: '2U'
    }, {
      label: '4U',
      value: '4U'
    }, {
      label: '塔式',
      value: '塔式'
    }, {
      label: '刀片',
      value: '刀片'
    }, {
      label: '其他',
      value: '其他'
    }]);
    const warrantyTypeOptions = ref([{
      label: '原厂保修',
      value: 'original'
    }, {
      label: '第三方保修',
      value: 'third_party'
    }]);

    // 资产责任人选项
    const userOptions = ref([{
      id: 1,
      label: '张三',
      value: 'zhangsan'
    }, {
      id: 2,
      label: '李四',
      value: 'lisi'
    }, {
      id: 3,
      label: '王五',
      value: 'wangwu'
    }, {
      id: 4,
      label: '赵六',
      value: 'zhaoliu'
    }]);

    // 供应商责任人选项
    const supplierManagerOptions = ref([]);

    // 供应商机构名变化处理
    const onSupplierOrgChange = value => {
      formData.value.supplier_manager = '';
      // 根据供应商机构名获取对应的责任人列表
      if (value === '华为技术有限公司') {
        supplierManagerOptions.value = [{
          label: '华为-张经理',
          value: 'huawei-zhang'
        }, {
          label: '华为-李经理',
          value: 'huawei-li'
        }];
      } else if (value === '浪潮集团有限公司') {
        supplierManagerOptions.value = [{
          label: '浪潮-王经理',
          value: 'inspur-wang'
        }, {
          label: '浪潮-赵经理',
          value: 'inspur-zhao'
        }];
      } else {
        supplierManagerOptions.value = [];
      }
    };
    const assetStatusOptions = ref([{
      label: '在用',
      value: 'in_use'
    }, {
      label: '报废',
      value: 'scrapped'
    }]);

    // 计算属性
    const dialogVisible = computed({
      get: () => props.visible,
      set: value => emit('update:visible', value)
    });
    const formTitle = computed(() => {
      return props.isEdit ? '编辑硬件设施' : '新增硬件设施';
    });

    // 移除了不必要的副标题计算属性

    const supplierContactOptions = computed(() => {
      return supplierContacts.value.map(contact => ({
        label: contact.name,
        value: contact.name
      }));
    });

    // 表单验证状态
    const isFormValid = computed(() => {
      return formData.asset_tag && formData.model && formData.manufacturer && formData.serial_number && formData.asset_owner && formData.purchase_date;
    });

    // 表单验证规则
    const formRules = {
      asset_tag: [{
        required: true,
        message: '请输入资产编号',
        trigger: 'blur'
      }, {
        min: 2,
        max: 50,
        message: '资产编号长度在 2 到 50 个字符',
        trigger: 'blur'
      }],
      model: [{
        required: true,
        message: '请输入设备型号',
        trigger: 'blur'
      }, {
        min: 2,
        max: 100,
        message: '设备型号长度在 2 到 100 个字符',
        trigger: 'blur'
      }],
      manufacturer: [{
        required: true,
        message: '请选择制造商',
        trigger: 'change'
      }],
      serial_number: [{
        required: true,
        message: '请输入序列号',
        trigger: 'blur'
      }, {
        min: 2,
        max: 100,
        message: '序列号长度在 2 到 100 个字符',
        trigger: 'blur'
      }],
      asset_owner: [{
        required: true,
        message: '请选择资产责任人',
        trigger: 'change'
      }],
      purchase_date: [{
        required: true,
        message: '请选择采购日期',
        trigger: 'change'
      }],
      warranty_type: [{
        required: true,
        message: '请选择保修类型',
        trigger: 'change'
      }],
      warranty_date_range: [{
        required: true,
        message: '请选择保修日期',
        trigger: 'change'
      }]
    };

    // 方法
    const resetForm = () => {
      Object.keys(formData).forEach(key => {
        if (key === 'monitoring_status') {
          formData[key] = false;
        } else if (key === 'warranty_type') {
          formData[key] = 'original';
        } else if (key === 'asset_status') {
          formData[key] = 'in_use';
        } else if (key === 'warranty_date_range') {
          formData[key] = [];
        } else {
          formData[key] = '';
        }
      });
      specifications.value = [{
        key: '',
        value: ''
      }];
      formRef.value?.resetFields();
    };
    const loadFormData = () => {
      if (props.assetData && props.isEdit) {
        Object.keys(formData).forEach(key => {
          if (props.assetData[key] !== undefined) {
            formData[key] = props.assetData[key];
          }
        });

        // 处理规格参数
        if (props.assetData.specifications && typeof props.assetData.specifications === 'object') {
          const specs = Object.entries(props.assetData.specifications).map(([key, value]) => ({
            key,
            value
          }));
          specifications.value = specs.length > 0 ? specs : [{
            key: '',
            value: ''
          }];
        }
      }
    };
    const loadUsers = async () => {
      if (userOptions.value.length > 0) return;
      userLoading.value = true;
      try {
        const response = await userApi.search({
          page_size: 100
        });
        userOptions.value = response.data.results || [];
      } catch (error) {
        console.error('Load users error:', error);
        message.error('加载用户列表失败');
      } finally {
        userLoading.value = false;
      }
    };
    const searchUsers = async query => {
      if (!query) return;
      userLoading.value = true;
      try {
        const response = await userApi.search({
          search: query,
          page_size: 50
        });
        userOptions.value = response.data.results || [];
      } catch (error) {
        console.error('Search users error:', error);
        message.error('搜索用户失败');
      } finally {
        userLoading.value = false;
      }
    };
    const loadSuppliers = async () => {
      if (supplierOptions.value.length > 0) return;
      supplierLoading.value = true;
      try {
        const response = await supplierApi.getSimpleList({
          page_size: 100
        });
        supplierOptions.value = response.data || [];
      } catch (error) {
        console.error('Load suppliers error:', error);
        message.error('加载供应商列表失败');
      } finally {
        supplierLoading.value = false;
      }
    };
    const searchSuppliers = async query => {
      supplierLoading.value = true;
      try {
        const params = query ? {
          search: query,
          page_size: 50
        } : {
          page_size: 100
        };
        const response = await supplierApi.getSimpleList(params);
        supplierOptions.value = response.data || [];
      } catch (error) {
        console.error('Search suppliers error:', error);
        message.error('搜索供应商失败');
      } finally {
        supplierLoading.value = false;
      }
    };
    const handleSupplierChange = async supplierId => {
      supplierContacts.value = [];
      if (!supplierId) return;
      try {
        const response = await supplierApi.getContacts(supplierId);
        supplierContacts.value = response.data || [];
      } catch (error) {
        console.error('Get supplier contacts error:', error);
        message.error('获取供应商联系人失败');
      }
    };
    const addSpec = () => {
      specifications.value.push({
        key: '',
        value: ''
      });
    };
    const removeSpec = index => {
      if (specifications.value.length > 1) {
        specifications.value.splice(index, 1);
      }
    };
    const filterOption = (input, option) => {
      return option.label.toLowerCase().includes(input.toLowerCase());
    };
    const disabledDate = current => {
      // 禁用未来日期
      return current && current > dayjs().endOf('day');
    };
    const validateSpecifications = () => {
      // 新增模式下不需要验证规格参数
      if (!props.isEdit) {
        return true;
      }
      const validSpecs = specifications.value.filter(spec => spec.key && spec.value);
      if (validSpecs.length === 0) {
        message.warning('请至少添加一个有效的规格参数');
        return false;
      }
      return true;
    };
    const handleSubmit = async () => {
      try {
        await formRef.value.validate();
        if (!validateSpecifications()) {
          return;
        }
        submitLoading.value = true;

        // 显示加载提示
        const loadingMessage = message.loading(props.isEdit ? '正在更新设备信息...' : '正在创建设备...', 0);

        // 处理规格参数（仅在编辑模式下）
        const submitData = {
          ...formData
        };
        if (props.isEdit) {
          const specs = {};
          specifications.value.forEach(spec => {
            if (spec.key && spec.value) {
              specs[spec.key] = spec.value;
            }
          });
          submitData.specifications = specs;
        }
        if (props.isEdit && props.assetData?.id) {
          await hardwareAssetApi.update(props.assetData.id, submitData);
          loadingMessage();
          message.success('设备信息更新成功！');
        } else {
          await hardwareAssetApi.create(submitData);
          loadingMessage();
          message.success('设备创建成功！');
        }
        emit('success');
        handleClose();
      } catch (error) {
        console.error('Submit error:', error);

        // 详细的错误处理
        let errorMessage = '操作失败，请重试';
        if (error.response) {
          const {
            status,
            data
          } = error.response;
          if (status === 400) {
            if (data && typeof data === 'object') {
              const errorMessages = [];
              Object.entries(data).forEach(([field, messages]) => {
                if (Array.isArray(messages)) {
                  errorMessages.push(...messages);
                } else {
                  errorMessages.push(messages);
                }
              });
              errorMessage = errorMessages.join(', ') || '请检查输入的数据格式';
            } else {
              errorMessage = data?.message || '请检查输入的数据格式';
            }
          } else if (status === 409) {
            errorMessage = '资产编号或序列号已存在，请使用其他值';
          } else if (status === 500) {
            errorMessage = '服务器内部错误，请稍后重试';
          }
        } else if (error.message) {
          errorMessage = error.message;
        }
        message.error(errorMessage);
      } finally {
        submitLoading.value = false;
      }
    };
    const handleSubmitFailed = errorInfo => {
      console.log('Form validation failed:', errorInfo);
      message.error('请检查表单填写是否正确');
    };
    const handleClose = () => {
      dialogVisible.value = false;
      nextTick(() => {
        resetForm();
      });
    };

    // 监听器
    watch(() => props.visible, newVal => {
      if (newVal) {
        nextTick(() => {
          if (props.isEdit) {
            loadFormData();
          } else {
            resetForm();
          }
        });
      }
    });

    // 生命周期
    onMounted(() => {
      loadUsers();
      loadSuppliers();
    });
    const __returned__ = {
      props,
      emit,
      formRef,
      submitLoading,
      userLoading,
      supplierLoading,
      supplierOptions,
      supplierContacts,
      specifications,
      formData,
      assetTagOptions,
      manufacturerOptions,
      dimensionOptions,
      warrantyTypeOptions,
      userOptions,
      supplierManagerOptions,
      onSupplierOrgChange,
      assetStatusOptions,
      dialogVisible,
      formTitle,
      supplierContactOptions,
      isFormValid,
      formRules,
      resetForm,
      loadFormData,
      loadUsers,
      searchUsers,
      loadSuppliers,
      searchSuppliers,
      handleSupplierChange,
      addSpec,
      removeSpec,
      filterOption,
      disabledDate,
      validateSpecifications,
      handleSubmit,
      handleSubmitFailed,
      handleClose,
      ref,
      reactive,
      computed,
      watch,
      nextTick,
      onMounted,
      get message() {
        return message;
      },
      get dayjs() {
        return dayjs;
      },
      get hardwareAssetApi() {
        return hardwareAssetApi;
      },
      get supplierApi() {
        return supplierApi;
      },
      get userApi() {
        return userApi;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
